import { Component } from "react";

class FeedBackWidget extends Component {
  state = {
    good: 0,
    neutral: 0,
    bad: 0,
    total: 0,
  };

  handleFeedback = (type) => {
    this.setState((prevState) => ({ [type]: prevState[type] + 1 }));
  };

  countTotalFeedback = () => {
    const { good, neutral, bad } = this.state;
    return good + neutral + bad;
  };

  countPositiveFeedbackPercentage = () => {
    const total = this.countTotalFeedback();
    const { good } = this.state;

    if (total === 0) return 0;

    return ((good / total) * 100).toFixed(2);
  };

  render() {
    const totalFeedBack = this.countTotalFeedback();
    const positiveFeedbackPercentage = this.countPositiveFeedbackPercentage();
    return (
      <div>
        <h2>Оставьте ваш отзыв</h2>
        <div>
          <button onClick={() => this.handleFeedback("good")} variant="success">
            Хорошо
          </button>
          <button
            onClick={() => this.handleFeedback("neutral")}
            variant="secondary"
          >
            Нейтрально
          </button>
          <button
            onClick={() => this.handleFeedback("bad")}
            variant="destructive"
          >
            Плохо
          </button>
        </div>
        <div>
          <div>
            <p>Хорошо: {this.state.good}</p>
            <p>Нейтрально: {this.state.neutral}</p>
            <p>Плохо: {this.state.bad}</p>
          </div>
          <p>Общее количество отзывов: {totalFeedBack}</p>
          <p>Процент положительных отзывов: {positiveFeedbackPercentage}%</p>
        </div>
      </div>
    );
  }
}

export { FeedBackWidget };
