import { Component } from "react";

class FeedBackWidget extends Component {
  state = {
    good: "",
    neutral: "",
    bad: "",
  };

  render() {
    return (
      <div className="flex flex-col items-center space-y-4 p-6">
        <h2 className="text-xl font-semibold">Оставьте ваш отзыв</h2>
        <div className="flex space-x-4">
          <button onClick={() => this.handleFeedback("good")} variant="success">
            Хорошо
          </button>
          <button
            onClick={() => this.handleFeedback("neutral")}
            variant="secondary"
          >
            Нейтрально
          </button>
          <button
            onClick={() => this.handleFeedback("bad")}
            variant="destructive"
          >
            Плохо
          </button>
        </div>
        <div className="w-64 text-center">
          <div className="p-4">
            <p>Хорошо: {this.state.good}</p>
            <p>Нейтрально: {this.state.neutral}</p>
            <p>Плохо: {this.state.bad}</p>
          </div>
        </div>
      </div>
    );
  }
}

export { FeedBackWidget };
